mapper.select:
SELECT COUNT(*)
FROM WEATHER
WHERE WEATHER_NO = #{weatherNo}

mapper.insert:
INSERT INTO WEATHER (WEATHER_NO
				   , BASE_DATE
				   , BASE_TIME
				   , FCST_DATE
				   , FCST_TIME
				   , TMP
				   , UUU
				   , VVV
				   , VEC
				   , WSD
				   , SKY
				   , PTY
				   , POP
				   , WAV
				   , PCP
				   , REH
				   , SNO
				   , TMN
				   , TMX)
			 VALUES (#{weatherNo}
			 	   , #{baseDate}
			 	   , #{baseTime}
			 	   , #{fcstDate}
			 	   , #{fcstTime}
			 	   , #{TMP}
			 	   , #{UUU}
			 	   , #{VVV}
			 	   , #{VEC}
			 	   , #{WSD}
			 	   , #{SKY}
			 	   , #{PTY}
			 	   , #{POP}
